var counter=0,increaseCounterCurrentScore=function(){window.setInterval(function(){counter<=score&&($("#current-score").text("Score : "+counter),counter++)},5)},decreaseCounterCurrentScore=function(){window.setInterval(function(){counter>=score&&($("#current-score").text("Score : "+counter),counter--)},5)},init=function(){counter>score?decreaseCounterCurrentScore():increaseCounterCurrentScore()};$(document).ready(init);